import { useEffect, useState } from "react"
import { useNavigate, useParams } from "react-router-dom"
import { getDoc, updateDoc, doc } from "firebase/firestore"
import { db } from "../firebaseConfig/firebase"

const Edit = () => {
    const [ heroe, setHeroe ] = useState('')
    const [ nombre, setNombre ] = useState('')
    const [ edad, setEdad ] = useState(0)
    const [ sexo, setSexo ] = useState('')
    const [ origen, setOrigen ] = useState('')
    const [ caracteristicas, setCaracteristias ] = useState('')

    const navigate = useNavigate()    
    const {id} = useParams()

    const update = async (e) => {
        e.preventDefault()
        const supers = doc(db, "superheroe", id)
        const data = { heroe: heroe, nombre: nombre, edad: edad, sexo: sexo, origen: origen, caracteristicas: caracteristicas }
        await updateDoc(supers, data)
        navigate('/')
    }

    const getProductById = async (id) => {
        const supers = await getDoc( doc(db, "superheroe", id) )
        if(supers.exists()) {
            //console.log(product.data())
            setHeroe(supers.data().heroe)   
            setNombre(supers.data().nombre)   
            setEdad(supers.data().edad)
            setSexo(supers.data().poderes)
            setOrigen(supers.data().origen)
            setCaracteristias(supers.data().caracteristicas)
        }else{
            console.log('El villano no existe')
        }
    }

    useEffect( () => {
        getProductById(id)
        // eslint-disable-next-line
    }, [])

    return (
        <div className='container'>
        <div className='row'>
            <div className='col'>
                <h1>Editar datos del Heroe</h1>
                 <form onSubmit={update}>
                 <div className='mb-3'>
                        <label className='form-label'>Heroe</label>
                        <input
                            value={heroe}
                            onChange={ (e)=> setVillano(e.target.value)} 
                            type="text"
                            className='form-control'
                        />                 
                    </div> 
                    <div className='mb-3'>
                        <label className='form-label'>Nombre</label>
                        <input
                            value={nombre}
                            onChange={ (e)=> setNombre(e.target.value)} 
                            type="text"
                            className='form-control'
                        />                 
                    </div> 
                    <div className='mb-3'>
                        <label className='form-label'>Edad</label>
                        <input
                            value={edad}
                            onChange={ (e)=> setEdad(e.target.value)} 
                            type="number"
                            className='form-control'
                        />                 
                    </div> 
                    <div className='mb-3'>
                        <label className='form-label'>Poderes</label>
                        <input
                            value={poderes}
                            onChange={ (e)=> setPoderes(e.target.value)} 
                            type="text"
                            className='form-control'
                        />                 
                    </div> 
                    <div className='mb-3'>
                        <label className='form-label'>Descripcion</label>
                        <input
                            value={descripcion}
                            onChange={ (e)=> setDescripcion(e.target.value)} 
                            type="text"
                            className='form-control'
                        />                 
                    </div> 
                    <div className='mb-3'>
                        <label className='form-label'>Enemigos</label>
                        <input
                            value={enemigos}
                            onChange={ (e)=> setEnemigos(e.target.value)} 
                            type="text"
                            className='form-control'
                        />                 
                    </div>  
                    <button type='submit' className='btn btn-primary'>Actualizar</button>
                 </form>   
            </div>
        </div>
    </div> 
    )
}

export default Edit;
