import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import {collection, getDocs, getDoc, deleteDoc, doc, addDoc} from 'firebase/firestore'
import { db } from '../firebaseConfig/firebase'
import Swal from 'sweetalert2'
import withReactContent from 'sweetalert2-react-content'
import { async } from '@firebase/util'
import Create from './Create'


import { useNavigate } from 'react-router-dom'

const MySwal = withReactContent(Swal)

const Show = () => {

  const [ heroe, setHeroe ] = useState('')
  const [ nombre, setNombre ] = useState('')
  const [ edad, setEdad ] = useState(0)
  const [ sexo, setSexo ] = useState('')
  const [ origen, setOrigen ] = useState('')
  const [ caracteristicas, setCaracteristias ] = useState('')

  const VillanosCollection = collection(db, "superheroe")

  const supers = async (e) => {
    e.preventDefault()
    await addDoc( VillanosCollection, { heroe: heroe, nombre: nombre, edad: edad, sexo: sexo, origen: origen, caracteristicas: caracteristicas } )
    window.location.href = window.location.href;
    window.location.replace('');
  }
  //1 - configuramos los hooks
  const [villanos, setVillanos] = useState( [] )

  //2 - referenciamos a la DB firestore

  //3 - Funcion para mostrar TODOS los docs
  const getVillanos = async ()   => {
   const data = await getDocs(VillanosCollection)
   //console.log(data.docs)
   setVillanos(
       data.docs.map( (doc) => ( {...doc.data(),id:doc.id}))
   )
   //console.log(products)
  }
  //4 - Funcion para eliminar un doc
  const deleteVillano = async (id) => {
   const villanoDoc = doc(db, "superheroe", id)
   await deleteDoc(villanoDoc)
   getVillanos()
  }
  //5 - Funcion de confirmacion para Sweet Alert 2
  const confirmDelete = (id) => {
    MySwal.fire({
      title: 'Â¿Elimina a el Villano?',
      text: "Boton rojo para eliminar",
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#d33',
      cancelButtonColor: '#3085d6',
      confirmButtonText: 'Si! Eliminar!'
    }).then((result) => {
      if (result.isConfirmed) { 
        //llamamos a la fcion para eliminar   
        deleteVillano(id)               
        Swal.fire(
          'Listo!',
          'Se elimino!',
          'success'
        )
      }
    })    
  }
  //6 - usamos useEffect
  useEffect( () => {
    getVillanos()
    // eslint-disable-next-line
  }, [] )
  //7 - devolvemos vista de nuestro componente
  return (

    <>
    <div class="container">
  <div class="row">
    <div class="col">
    <h1>Heroe</h1>
                 <form onSubmit={supers}>
                    <div className='mb-3'>
                        <label className='form-label'>Nombre Real</label>
                        <input
                            value={heroe}
                            onChange={ (e)=> setHeroe(e.target.value)} 
                            type="text"
                            className='form-control'
                        />                 
                    </div>
                    <div className='mb-3'>
                        <label className='form-label'>Nombre Villano</label>
                        <input
                            value={nombre}
                            onChange={ (e)=> setNombre(e.target.value)} 
                            type="text"
                            className='form-control'
                        />                 
                    </div>  
                    <div className='mb-3'>
                        <label className='form-label'>Edad</label>
                        <input
                            value={edad}
                            onChange={ (e)=> setEdad(e.target.value)} 
                            type="number"
                            className='form-control'
                        />                 
                    </div>  
                    <div className='mb-3'>
                        <label className='form-label'>Sexo</label>
              <input type="radio" name='sexo' value='Hombre' onChange={(e) => setSexo(e.target.value)} />
              <p>Mujer</p>
              <input type="radio" name='sexo' value='Mujer' onChange={(e) => setSexo(e.target.value)} />
              <p>No Especificado</p>
              <input type="radio" name='sexo' value='No Especificado' onChange={(e) => setSexo(e.target.value)} />
                    </div>  
                    <div className='mb-3'>
                        <label className='form-label'>Origen</label>
                        <input
                            value={origen}
                            onChange={ (e)=> setOrigen(e.target.value)} 
                            type="text"
                            className='form-control'
                        />                 
                    </div>  
                    <div className='mb-3'>
                        <label className='form-label'>Caracteristicas</label>
                        <input
                          value={origen}
                            onChange={ (e)=> setOrigen(e.target.value)} 
                            type="text"
                            className='form-control'
                        />                    
                    </div>  

                    <button type='submit' className='btn btn-warning'>AÃ±adir</button>
                 </form>   
    </div>
    <div class="col">
    <table className='table table-dark table-hover'>
            <thead>
              <tr>
                <th>Nombre Real</th>
                <th>Nombre Villano</th>
                <th>Edad</th>
                <th>Sexo</th>
                <th>Origen</th>
                <th>Caracteristicas</th>
              </tr>
            </thead>
            <tbody>
              { villanos.map( (villano) => (
                <tr key={villano.id}>
                  <td>{villano.heroe}</td>
                  <td>{villano.nombre}</td>
                  <td>{villano.edad}</td>
                  <td>{villano.origen}</td>
                  <td>{villano.caracteristicas}</td>
                  <td>
                    <Link to={`/editv/${villano.id}`} className="btn btn-light"><i className="fa-solid fa-pencil"></i></Link>
                    <button onClick={ () => { confirmDelete(villano.id) } } className="btn btn-warning"><i className="fa-solid fa-trash"></i></button>
                  </td>
                </tr>                
              )) }
            </tbody>
          </table>
    </div>
    </div>
  </div>


    </>
  )
}
export default Show
